CREATE TABLE tournaments (
    tournament_id NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    name VARCHAR2(100) NOT NULL,
    tournament_date DATE,
    prize_money NUMBER,
    country VARCHAR2(100),
    surface_id NUMBER,
    tournament_type_id NUMBER,
    CONSTRAINT fk_surface
        FOREIGN KEY (surface_id)
        REFERENCES surfaces (surface_id),
    CONSTRAINT fk_tournament_type
        FOREIGN KEY (tournament_type_id)
        REFERENCES tournament_types (tournament_type_id)
);

